    public class MKVMergeLanguageService : IMKVMergeLanguageService
    {
        private List<MKVMergeLanguageItem> _languages;
        private IJsonSerializationService<ISOLanguageCodeCollection> _jsonSerializationService;

        public MKVMergeLanguageService(IJsonSerializationService<ISOLanguageCodeCollection> jsonSerializationService)
        {
            _jsonSerializationService = jsonSerializationService;
            _languages = GetMKVMergeLanguageItems();
        }


        public MKVMergeLanguageItem GetLanguageByName(string name)
        {
            MKVMergeLanguageItem item = _languages.FirstOrDefault(l => l.Language.ToLower() == name.ToLower());
            if (item != null)
                return item;
            else
                return _languages.FirstOrDefault(l => l.Language == "Undetermined");
        }

        public List<MKVMergeLanguageItem> GetLanguages()
        {
            return _languages;
        }

