    public class CountryCodeService : ICountryCodeService
    {
        private List<CountryCodeItem> _countryCodes;
        private IJsonSerializationService<ISOCountryCodeCollection> _jsonSerializationService;

        public CountryCodeService(IJsonSerializationService<ISOCountryCodeCollection> jsonSerializationService)
        {
            _jsonSerializationService = jsonSerializationService;
            _countryCodes = this.GetCountryCodeItems();
        }

        public CountryCodeItem GetCountryCodeByCountry(string country)
        {
            if (string.IsNullOrEmpty(country))
                return null;

            return _countryCodes.Where(c => c.Name.ToLower() == country.ToLower()).Single();
        }

        public List<CountryCodeItem> GetCountryCodes()
        {
            return _countryCodes;
        }

        private List<CountryCodeItem> GetCountryCodeItems()
        {
            _countryCodes = new List<CountryCodeItem>();
            var isoCountryCodes = this.GetISOCountryCodes();

            foreach (var isoCountryCode in isoCountryCodes)
            {
                _countryCodes.Add(new CountryCodeItem() { CountryCode = isoCountryCode.countrycode, Name = isoCountryCode.name, Value = isoCountryCode.alpha3 });
            }

            return _countryCodes;
        }

